package day3;

public class SwapnumbersM2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10;
int b=20;

System.out.println("Before swapping: a=" + a + ", b=" + b);
a=a+b; //30
b=a-b;
a=a-b;

System.out.println("After swapping: a=" + a + ", b=" + b);

	}
}

