package day5;

public class Whilecodedemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int j=11;
    while (j<=20) {
		if (j%2==0) {
		System.out.println(j+"even number ");	
		}
		else {
			System.out.println(j+"odd number");
		
		}
		j++;
	}
	}

}
